package com.example.homeforpaws

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.GridLayoutManager
import com.example.homeforpaws.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    private lateinit var binding:ActivityMainBinding
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)
        val itemList = ArrayList<MainListItem>()
        itemList.add(MainListItem("강아지","data:image/jpeg;base64,"))
        itemList.add(MainListItem("강아지2","data:image/jpeg;base64,"))
        itemList.add(MainListItem("강아지3","data:image/jpeg;base64,"))
        itemList.add(MainListItem("강아지4","data:image/jpeg;base64,"))

        val mainListAdapter = MainListAdapter(itemList)
        mainListAdapter.notifyDataSetChanged()
        binding.mainListRecyclerView.adapter = mainListAdapter
        binding.mainListRecyclerView.layoutManager = GridLayoutManager(this,2)
    }
}